import { countingValleys } from "../countingValleys";

describe("CountingValleys", () => {
  it("Should return", () => {
    const result = countingValleys(8, "UDDDUDUU");
    expect(result).toEqual(1);
  });
  it("Should return", () => {
    const result = countingValleys(12, "DDUUDDUDUUUD");
    expect(result).toEqual(1);
  });
});

